import React from 'react';
import {fetchCitiesWithRedux} from "../actions/cityActions";
import {connect} from 'react-redux';

class Cities extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            cityName: "",
            cities: this.props.cities
        }
    }

    componentDidMount() {
        this.props.dispatch(fetchCitiesWithRedux());
    };

    handleChange = (e) => {
        this.setState({
            cityName: e.target.value
        });
        if (e.key === 'Enter') {
            console.log(this.state.cityName);
            console.log(this.state.cities);
            this.setState({
                cities: this.state.cities.filter(city => city.name.toLocaleLowerCase().includes(this.state.cityName.toLocaleLowerCase()))
            });
            // this.props.cities = this.props.cities.filter(city => city.name.toLocaleLowerCase().includes(this.state.cityName.toLocaleLowerCase()));
        }
    };

    listItems = () => {
        const listItems = this.state.cities.map((city) =>
            <div className="cityPhotoHolder" key={city._id}>
                <p className="cityName">{city.name}</p>
                <img className="cityDisplay" src={city.src} alt="img"/>
            </div>
        );
        return listItems;
    };

    render() {
        const searchIcoStyle = {
            height: 17
        };
        return (
            <div className="contain cityDisplay">
                <div className="section searchBar">
                    <input id="searchText" type="text" value={this.state.cityName} onChange={this.handleChange} onKeyPress={this.handleChange}/>
                    <div className="searchIconHolder"><img style={searchIcoStyle}
                                                           src={require("../images/search-ico.png")} alt="search"/>
                    </div>
                </div>
                {this.listItems()}
            </div>
        )
    }
}


const mapStateToProps = state => ({
    cities: state.cityReducer.cities,
    loading: state.cityReducer.loading,
    error: state.cityReducer.error,
});

export default connect(mapStateToProps)(Cities)
