import React from 'react';
import { fetchCitiesWithRedux } from "../actions/cityActions";
import {connect} from 'react-redux';

class Cities extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            cityName: "",
        }
    }
    componentDidMount(){
        this.props.dispatch(fetchCitiesWithRedux());
    };

    handleChange = (e) => {
        this.setState({
            cityName: e.target.value
        });
        // console.log(this.state.cityName);
        this.props.cities.onChange(e.target.value)
    };

    listItems = () => {
        const listItems = this.props.cities.filter(city=>city.name.includes(this.state.cityName.toLocaleLowerCase())).map((city) =>
            <div className="cityPhotoHolder" key={city._id}>
                <p className="cityName">{city.name}</p>
                <img className="cityDisplay" src={city.src} alt="img"/>
            </div>
        );
        return listItems;
    };

    render() {
        return (
        <div className="contain cityDisplay">
            <label htmlFor="searchText">Search</label><input id="searchText" type="text" value={this.state.cityName}
                                                             onChange={this.handleChange}/>
            {this.listItems()}
        </div>
    )
    }
}


const mapStateToProps = state => ({
    cities: state.cityReducer.cities,
    loading: state.cityReducer.loading,
    error: state.cityReducer.error,
});

export default connect(mapStateToProps)(Cities)
