import React from 'react';
import {fetchCitiesWithRedux} from "../actions/cityActions";
import {connect} from 'react-redux';
import {filterCities} from "../actions/cityActions";

class Cities extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            cityName: ""
        }
    }

    componentDidMount() {
        this.props.dispatch(fetchCitiesWithRedux());
    };

    handleChange = (e) => {
        this.setState({
            cityName: e.target.value
        });
        if (e.key === 'Enter') {
            console.log(this.state.cityName);
            this.props.filterCities(this.state.cityName);
            // this.props.cities = this.props.cities.filter(city => city.name.toLocaleLowerCase().includes(this.state.cityName.toLocaleLowerCase()));
        }
    };

    listItems = () => {
        const listItems = this.props.cities.map((city) =>
            <div className="cityPhotoHolder" key={city._id}>
                <p className="cityName">{city.name}</p>
                <img className="cityDisplay" src={city.src} alt="img"/>
            </div>
        );
        return listItems;
    };

    render() {
        const searchIcoStyle = {
            height: 17
        };
        return (
            <div className="contain cityDisplay">
                <div className="section searchBar">
                    <input id="searchText" type="text" value={this.state.cityName} onChange={this.handleChange}
                           onKeyPress={this.handleChange}/>
                    <div className="searchIconHolder"><img style={searchIcoStyle}
                                                           src={require("../images/search-ico.png")} alt="search"/>
                    </div>
                </div>
                {this.listItems()}
            </div>
        )
    }
}


const mapStateToProps = state => ({
    cities: state.cityReducer.cities,
    loading: state.cityReducer.loading,
    error: state.cityReducer.error,
});

// const mapDispatchToProps = (dispatch) => {
//     return {
//         filterCities: (cityName) => {
//             dispatch(filterCities(cityName))
//         }
//     }
// };

const mapDispatchToProps = (dispatch) => {
    return {
        filterCities: (cityName) => {dispatch(filterCities(cityName))}
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(Cities)
