function getCities() {
    return fetch("http://localhost:5000/cities")
        .then(handleErrors)
        .then(res => res.json());
}

function fakeGetCities() {
    return new Promise(resolve => {
        // Resolve after a timeout so we can see the loading indicator
        setTimeout(
            () =>
                resolve(),
            1000
        );
    });
}

export function fetchCities() {
    return dispatch => {
        dispatch(fetchCitiesBegin());
        return fakeGetCities()
            .then(json => {
                dispatch(fetchCitiesSuccess(json.cities));
                return json.cities;
            })
            .catch(error =>
                dispatch(fetchCitiesFailure(error))
            );
    };
}

// Handle HTTP errors since fetch won't.
function handleErrors(response) {
    if (!response.ok) {
        throw Error(response.statusText);
    }
    return response;
}

export const FETCH_CITIES_BEGIN = "FETCH_CITIES_BEGIN";
export const FETCH_CITIES_SUCCESS =
    "FETCH_CITIES_SUCCESS";
export const FETCH_CITIES_FAILURE =
    "FETCH_CITIES_FAILURE";

export const fetchCitiesBegin = () => ({
    type: FETCH_CITIES_BEGIN
});

export const fetchCitiesSuccess = cities => ({
    type: FETCH_CITIES_SUCCESS,
    payload: { cities }
});

export const fetchCitiesFailure = error => ({
    type: FETCH_CITIES_FAILURE,
    payload: { error }
});
