                {/*<div key={index} className="test">*/}
                {/*<img src={article.src} alt=""/>*/}
                {/*</div>)}*/}
                {/*</Slider>*/}
                {/*<div className="cityDisplay">*/}
                {/*{this.display()}*/}
                {/*</div>*/}
                {/*</div>*/}
            </div>
        );
    }
}

export default Home;
