const rootReducer = (state = initState, action) => {
    switch (action.type) {
        case "FETCH_REQUEST":
            return state;
        case "FETCH_SUCCESS":
            return {...state, cities: action.payload};
        default:
            return state;
    }
};
export default rootReducer;
